package class_3;

public class Bitwise_Operator {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		// Bitwise AND
		int a=6,b=4;
		System.out.println("a&b="+ (a&b));
		
		//Bitwise OR
		int x=4,y=8;
		System.out.println("x|y="+(x|y));
		
		//Bitwise negation ~
		int z=1;
		System.out.println("~z="+(~z));
		
		//Bitwise XOR
		
	}

}
